        c1.setEntityInfo(c.getEntityInfo());
        s.addChain(c1);
        for (Group g : c.getAtomGroups()) {
            Atom a = null;
            switch(g.getType()) {
                case AMINOACID:
                    a = g.getAtom(StructureTools.CA_ATOM_NAME);
                    break;
                case NUCLEOTIDE:
                    a = g.getAtom(StructureTools.NUCLEOTIDE_REPRESENTATIVE);
                    break;
                default:
            }
            if (a != null) {
                Group g1 = (Group) g.clone();
                g1.clearAtoms();
                g1.addAtom(a);
                c1.addGroup(g1);
            }
        }
    }
    return s;
