{
    int i, j;
    double temp;
    /*
		 ** solve Fb =y
		 */
    for (i = 0; i < n; i++) {
        temp = y[i];
        for (j = 0; j < i; j++) {
            temp -= y[j] * matrix[i][j];
        }
        y[i] = temp;
    }
    /*
		 ** solve DF'z =b
		 */
    for (i = (n - 1); i >= 0; i--) {
        if (matrix[i][i] == 0) {
            y[i] = 0;
        } else {
            temp = y[i] / matrix[i][i];
            for (j = i + 1; j < n; j++) {
                temp -= y[j] * matrix[j][i];
            }
            y[i] = temp;
        }
    }
}
