            s = s.trim();
            if (s.length() > 0) {
                if (s.charAt(0) == '#') {
                    //ignore comment lines
                    if (s.startsWith("##fasta"))
                        break;
                } else {
                    FeatureI f = parseLine(s);
                    if (f != null) {
                        features.add(f);
                    }
                }
            }
        }
    }
    return features;
