        group.setResidueNumber(chain.getName().trim(), groupNumber, null);
    } else {
        group.setResidueNumber(chain.getName().trim(), groupNumber, insertionCode);
    }
    group.setAtoms(new ArrayList<>(atomCount));
    if (polymerType == 1 || polymerType == 2) {
