    if (-1 - 1e-8 < c && c < -1)
        c = -1;
    if (1 + 1e-8 > c && c > 1)
        c = 1;
    if (-1 > c || c > 1) {
        throw new IllegalArgumentException("Input matrix is not a valid rotation matrix.");
