    ChemComp chemComp = g.getChemComp();
    if (chemComp == null) {
        logger.warn("Can't determine PolymerType for group " + g.getResidueNumber() + " (" + g.getPDBName() + "). Will consider it as non-nucleotide/non-protein type.");
        return false;
    }
    PolymerType polyType = chemComp.getPolymerType();
    for (PolymerType protOnlyType : PolymerType.PROTEIN_ONLY) {
        if (polyType == protOnlyType)
            return true;
    }
    for (PolymerType protOnlyType : PolymerType.POLYNUCLEOTIDE_ONLY) {
        if (polyType == protOnlyType)
            return true;
    }
    return false;
}
