    // Get all the atoms
    List<Atom> theseAtoms = new ArrayList<Atom>();
    for (int i = 0; i < bioJavaStruct.nrModels(); i++) {
        List<Chain> chains = bioJavaStruct.getModel(i);
        for (Chain c : chains) {
            for (Group g : c.getAtomGroups()) {
                for (Atom a : MmtfUtils.getAtomsForGroup(g)) {
                    theseAtoms.add(a);
                }
            }
        }
    }
    return theseAtoms;
}
